import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { observer, PropTypes as ObservabelPropTypes } from 'mobx-react'
import './index.scss'

@observer
class TodoList extends Component {
  static propTypes = {
    addToto: PropTypes.func,
    store: PropTypes.shape({
      todos: ObservabelPropTypes.observableArrayOf(ObservabelPropTypes.observableObject).isRequired
    })

  }
  state = {
    inputVal :''
  }

  handleSubmit = (e) => {
    e.preventDefault()
    const store = this.props.store
    const inputVal = this.state.inputVal
    store.addToto(inputVal)
    this.setState({
      inputVal: ''
    })

  }
  haneldChange = (e) => { 
    const inputVal = e.target.value
    this.setState({
      inputVal
    })
  }
  render() {

    return (
      <div className="todo-list">
        <header>
          <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              className="input"
              placeholder="What needs to finished ?"
              value={this.state.inputVal}
              onChange={this.haneldChange}

            />
          </form>
        </header>
        <ul></ul>
        <footer></footer>
        <button>Refresh</button>
      </div>
    )
  }
}
export default TodoList
