import React, { Component, Fragment} from "react";
import { observable, action, computed } from 'mobx'
import PropTypes from 'prop-types'
import { observer, PropTypes as ObservablePropTypes } from 'mobx-react'

// import Page from './pages/Index'
import "./App.scss"
class Todo { 
  @observable id = Math.random();
  @observable title = ""
  @observable finished = false

  constructor (title) {
    this.title =  title;
  }

  @action finishedToggle() {
    this.finished = !this.finished;
  }
}
class Store { 
  @observable todos = [];
  @action.bound addTodo(title) {    
    this.todos.unshift(new Todo(title));
  }
  // 计算属性
  @computed get unfinished() { 
    return this.todos.filter(todo=>!todo.finished).length
  }
}

const store = new Store()

class TodoItem extends Component { 
  static propTypes = {
    // 传入todo
    todo: PropTypes.shape({
      id: PropTypes.number.isRequired,
      title: PropTypes.string.isRequired,
      finished: PropTypes.bool.isRequired,
      finishedToggle:PropTypes.func
    }).isRequired,
    // 传入store
    store: PropTypes.shape({
      todos: PropTypes.array.isRequired,
      delTodo:PropTypes.func.isRequired
    }).isRequired
  }

  render() {
    const store = this.props.store
    const todo = this.props.todo
    return (
      <Fragment>
        <input
          type="checkbox"
          className="toggle"
          onChange={() => todo.finishedToggle()}
          checked={todo.finished}
        />
        <span>{todo.title}</span>
        <span></span>
      </Fragment>
    )
  }
}
@observer
class TodoList extends Component {
  static propTypes = {
    store: PropTypes.shape({
      // 对store内的数据进行类型校验
      addTodo: PropTypes.func,
      // todos为observableArray，其内数据为observableObject，isRequire表示store、todos必须存在
      todos: ObservablePropTypes.observableArrayOf(ObservablePropTypes.observableObject).isRequired
    }).isRequired

  }
  state = {
    inputVal :''
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const store = this.props.store
    const inputVal = this.state.inputVal
    store.addTodo(inputVal)
    this.setState({
      inputVal: ''
    })

  }
  handleChange = (e) => { 
    const inputVal = e.target.value
    this.setState({
      inputVal
    })
  }
  render() {
    const store = this.props.store
    const todos = store.todos
    return (
      <div className="todo-list">
        <header>
          <form onSubmit={this.handleSubmit}>
            <input
              type="text"
              className="input"
              placeholder="What needs to finished ?"
              value={this.state.inputVal}
              onChange={this.handleChange}
            />
          </form>
        </header>
        <ul>
          {
            todos.map(todo => <li key={todo.id}>
              <TodoItem todo={todo} />
            </li>)
          }
        </ul>
        <footer> {store.unfinished} item(s) is unfinished !</footer>
        <button>Refresh</button>
      </div>
    )
  }
}
class App extends Component {
  render() {
    return <div>

      <TodoList store={store} />
    </div>;
  }
}
export default App
